mapper.select:
SELECT MAX(bid_price) AS bid_price
FROM BID
WHERE auction_id = #{auction_id}

SELECT buyer_id
FROM BID
WHERE auction_id = #{auction_id} 
AND bid_price = #{bid_price}

SELECT count(*)
FROM BID
WHERE auction_id = #{auction_id}

SELECT *
FROM BID
WHERE auction_id = #{auction_id}
AND bid_price = #{bid_price}
