mapper.update:
DELETE FROM likes
WHERE transaction = 1
and transaction_id= #{item_id}

update ITEM
set reserve = #{member_id}
where item_id = #{item_id}

update ITEM
set CONFIRM = 1
where item_id = #{item_id}
and member_id = #{member_id}

update ITEM
set reserve = '1'
where item_id = #{item_id}

mapper.select:
select MAX(item_id)
from ITEM
where member_id = #{member_id}
